import { Component } from "react";


export default class AddPostComp extends Component
{
    constructor()
    {
        super();
        this.state = {newTitle:'' , newBody:''}
    }

    createNewPost = () =>
    {
        const newPost = {
            userId:this.props.userId,
            id:this.props.postsArray.length+1,
            title:this.state.newTitle,
            body:this.state.newBody
        }
        this.props.changeDisplay(true);
        this.props.addPost(newPost);
    }
    
    render()
    {
        return(<div style={{border:"1px black solid",width:"200px",height:"200px",position:"absolute",left:"125px",bottom:"100px",borderRadius:"10px",padding:"15px"}}>
            <div>Title:<input type="text" onChange={(e) => this.setState({newTitle:e.target.value})}/></div>
            <div>Body:<input type="text" onChange={(e) => this.setState({newBody:e.target.value})}/></div>
            <div>
                <button onClick={this.createNewPost}>Add</button>
                <button style={{marginLeft:"10px",marginTop:"10px"}} onClick={() => this.props.changeDisplay(false)}>Cancel</button>
            </div>
        </div>)
    }
}
