import { Component } from "react";


export default class AddTodoComp extends Component
{
    constructor()
    {
        super();
        this.state = {newTitle:""}
    }

    /* Creates a new Todo and sends it to the parent */
    createNewTodo = () =>
    {
        const newTodo = {
            userId:this.props.userId,
            id:this.props.todosArray.length+1,
            title:this.state.newTitle,
            completed:false
        }
        this.props.changeDisplay(true); /* Returns true in order to update the todos details of the new User */
        this.props.addTodo(newTodo);
    }
    render()
    {
        return(<div style={{border:"1px black solid",width:"200px",height:"200px",position:"absolute",top:"50px",left:"125px",borderRadius:"10px",padding:"15px"}}>
            Title:<input type="text" onChange={(e) => this.setState({newTitle:e.target.value})}/>
            <button onClick={this.createNewTodo}>Add</button>
            <button style={{marginLeft:"10px",marginTop:"10px"}}onClick={() => this.props.changeDisplay(false)}>Cancel</button>
        </div>)
    }
}
