import { Component } from "react"

export default class AddUserComp extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {name:"",email:""}
    }

    //Changing states
    handleChange = (e) =>
    {
        const {value} = e.target
        this.setState({[e.target.name]:value})
    }

    /* On click on add user it calls the callback mainpage of the add user */
    addUser = () =>
    {
        const newUser = {
            id:"",
            name:this.state.name,
            email:this.state.email,
            address:{
                city:"",
                street:"",
                zipcode:""
            }
        }
        this.props.add(newUser)
    }

    render()
    {
        return(<div style={{display:this.props.display ? "block" : "none"}}>
            
            <div id="addUserBlock">
            <div id="adduser-title">Add New User </div>
                Name: <input type="text" name="name" onChange={this.handleChange}/> <br/>
                Email: <input type="text" name="email" onChange={this.handleChange}/> <br/>
                <button onClick={() => this.props.hide()}>Cancel</button>
                <button onClick={this.addUser}>Add</button>
            </div>

        </div>)
    }
}
